import React, { useState } from 'react';

function Navbar({ smoothScroll }) {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div className="nav" id="sticker">
      <label htmlFor="toggle" onClick={toggleMenu}>&#9776;</label>
      <input type="checkbox" id="toggle" checked={isMenuOpen} onChange={toggleMenu} />
      <div className={`menu ${isMenuOpen ? 'open' : ''}`}>
        <h5 className="logo"></h5>
        <a href="#inicio" onClick={smoothScroll}>inicio</a>
        <a href="#servicio" onClick={smoothScroll}>servicio</a>
        <a href="#trabajo" onClick={smoothScroll}>trabajo</a>
        <a href="#contacto" onClick={smoothScroll}>contacto</a>
      </div>
    </div>
  );
}

export default Navbar;
